import React, { Component } from "react";
import "./App.css";

class App extends Component {
  constructor() {
    super();
    this.state = {
      city: '',
      state:'',
      country: ''
    };
    this.handleChange = this.handleChange.bind(this);
  }

  handleChange(e){
    this.setState({[e.target.name]: e.target.value});}

    getLocation=async()=>{
      const apilocationcall=await fetch("https://api.db-ip.com/v2/free/self");
     const data=await apilocationcall.json();
     console.log(data.city, data.stateProv, data.countryName);
     this.setState(
       {city:data.city,
       state:data.stateProv,
       country:data.countryName}
     );
    }

  getWeather= async()=>{
    if(this.state.city==='Tiruchi'){
      const apicall=await fetch(`http://api.openweathermap.org/data/2.5/weather?q=Trichy,${this.state.country}&appid=ea695ae24c843680f5719fc33376a096&units=metric`);
    const data= await apicall.json();
    console.log(data);
    }else{
    const apicall=await fetch(`http://api.openweathermap.org/data/2.5/weather?q=${this.state.city},${this.state.country}&appid=ea695ae24c843680f5719fc33376a096&units=metric`);
    const data= await apicall.json();
    console.log(data);}
  }

  render() {
    this.getLocation();
    this.getWeather();
    return (
      <div className="App">
        <header className="App-header">
          <h2>Weather App</h2>
        </header>
        <h3>You are in {this.state.city}, {this.state.state}, {this.state.country}!</h3>
      </div>
    );
  }
}

export default App;
