import React, {Component} from "react";
import "../App.css";

class Info extends Component {
  constructor() {
    super();
    this.state={
        city:'',
        country:'',
        info:{},
    }
    this.getInfo=this.getInfo.bind(this);
  }

  async getInfo(){
    const apicall=await fetch(`http://api.openweathermap.org/data/2.5/forecast?q=${this.state.city},${this.state.country}&appid=1db31d64a574b28b83f890456cee97ce`);
    const data= await apicall.json();
    console.log(data);
    if(data) {this.setState({info:data});}
}

componentDidMount() {
    var pathArray = window.location.pathname.split('/');
    this.setState({
        city:pathArray[2],
        country:pathArray[3]
    })
    this.getInfo();
  }

render() {
    const weatherinfo = this.state.info?.list.map(i => {
        console.log('hi');
        return(<div>
            <h3>{i.dt_txt}</h3>
            <table><tbody>
            <tr><th>Parameter</th><th>Value</th></tr>
              <tr><td>Temperature</td><td>{i.main.temp}</td></tr>
              <tr><td>Maximum Temperature</td><td>{i.main.maxtemp}</td></tr>
              <tr><td>Minimum Temperature</td><td>{i.main.mintemp}</td></tr>
              <tr><td>Humidity</td><td>{i.main.humidity}</td></tr>
              <tr><td>Pressure</td><td>{i.main.pressure}</td></tr>
              <tr><td>Wind Speed</td><td>{i.wind.speed}</td></tr>
              <tr><td>Description</td><td>{i.weather[0].description}</td></tr>
              </tbody></table></div>
        )
    });
    return (
      <div>
          {weatherinfo}
      </div>
    );
  }
}

export default Info;
