import React from 'react';

var data;

async function getInfo(city, country){
    const apicall=await fetch(`http://api.openweathermap.org/data/2.5/forecast?q=${city},${country}&appid=1db31d64a574b28b83f890456cee97ce`);
    data= await apicall.json();
    console.log(data);
    for(var i=0;i<40;i++){
        console.log('hi');
        return(<div>
            <h3>{data.list[i].dt_txt}</h3>
            <table><tbody>
            <tr><th>Parameter</th><th>Value</th></tr>
              <tr><td>Temperature</td><td>{data.list[i].main.temp}</td></tr>
              <tr><td>Maximum Temperature</td><td>{data.list[i].main.maxtemp}</td></tr>
              <tr><td>Minimum Temperature</td><td>{data.list[i].main.mintemp}</td></tr>
              <tr><td>Humidity</td><td>{data.list[i].main.humidity}</td></tr>
              <tr><td>Pressure</td><td>{data.list[i].main.pressure}</td></tr>
              <tr><td>Wind Speed</td><td>{data.list[i].wind.speed}</td></tr>
              <tr><td>Description</td><td>{data.list[i].weather[0].description}</td></tr>
              </tbody></table></div>
        )
    }
}

const Info = () => {
    var pathArray = window.location.pathname.split('/');
    var city=pathArray[2];
    var country=pathArray[3];
    getInfo(city,country); 
    return (<div>
        <h2>5 day forecast for {city},{country} </h2>
        {getInfo(city,country)}
    </div>
    );
};

export default Info;
