import React, { Component } from "react";
import "./App.css";

class App extends Component {
  constructor() {
    super();
    this.state = {
      city: '',
      state:'',
      country: ''
    };
    this.handleChange = this.handleChange.bind(this);
  }

  handleChange(e){
    this.setState({[e.target.name]: e.target.value});}

    getLocation=async()=>{
      const apilocationcall=await fetch("https://api.db-ip.com/v2/free/self");
     const data=await apilocationcall.json();
     console.log(data);
     this.setState={
       city:data.city,
       state:data.stateProv,
       country:data.countryName
     }
    }

  getWeather= async(e)=>{
    e.preventDefault();
    console.log("hi");
    const city = e.target.elements.city.value;
    const country = e.target.elements.country.value;
    console.log(city,country);
    const apicall=await fetch(`http://api.openweathermap.org/data/2.5/weather?q=${city},${country}&appid=ea695ae24c843680f5719fc33376a096&units=metric`);
    const data= await apicall.json();
    console.log(data);
  }

  render() {
    this.getLocation();
    return (
      <div className="App">
        <header className="App-header">
          <h2>Weather App</h2>
        </header>
        <h3>You are in ${this.state.city}, ${this.state.state}, ${this.state.country}!</h3>
        <form onSubmit={this.getWeather}>
          <label htmlFor="city">City</label>
          <input type="text" name="city" value={this.state.city} placeholder="Enter City" onChange={this.handleChange}/>
          <label htmlFor="country">Country</label>
          <input type="text" name="country" value={this.state.country} placeholder="Enter Country" onChange={this.handleChange}/>
          <button>Get Weather</button>
        </form>
      </div>
    );
  }
}

export default App;
