import React from 'react';

var data;

async function getInfo(city, country){
    const apicall=await fetch(`http://api.openweathermap.org/data/2.5/forecast?q=${city},${country}&appid=1db31d64a574b28b83f890456cee97ce`);
    data= await apicall.json();
    console.log(data);
}

function displayInfo(number){
    for(var i=number;i<number+8;i++){
        return(<div>
            <h3>{data.list[number].dt_txt}</h3>
            <table><tbody>
            <tr><th>Parameter</th><th>Value</th></tr>
              <tr><td>Temperature</td><td>{data.list[number].main.temp}</td></tr>
              <tr><td>Maximum Temperature</td><td>{data.list[number].main.maxtemp}</td></tr>
              <tr><td>Minimum Temperature</td><td>{data.list[number].main.mintemp}</td></tr>
              <tr><td>Humidity</td><td>{data.list[number].main.humidity}</td></tr>
              <tr><td>Pressure</td><td>{data.list[number].main.pressure}</td></tr>
              <tr><td>Wind Speed</td><td>{data.list[number].wind.speed}</td></tr>
              <tr><td>Description</td><td>{data.list[number].weather[0].description}</td></tr>
              </tbody></table></div>
        )
    }
}

const Info = () => {
    var pathArray = window.location.pathname.split('/');
    var city=pathArray[1];
    var country=pathArray[2];
    getInfo(city,country); 
    displayInfo(0);
    displayInfo(8);
    displayInfo(16);
    displayInfo(24);
    displayInfo(32);
    return (<div>
    </div>
    );
};

export default Info;
