}

render() {
    const info = this.state.info?.list.map(i => {
        console.log('hi');
        return(<div>
            <h3>{i.dt_txt}</h3>
            <table><tbody>
            <tr><th>Parameter</th><th>Value</th></tr>
              <tr><td>Temperature</td><td>{i.main.temp}</td></tr>
              <tr><td>Maximum Temperature</td><td>{i.main.maxtemp}</td></tr>
              <tr><td>Minimum Temperature</td><td>{i.main.mintemp}</td></tr>
              <tr><td>Humidity</td><td>{i.main.humidity}</td></tr>
              <tr><td>Pressure</td><td>{i.main.pressure}</td></tr>
              <tr><td>Wind Speed</td><td>{i.wind.speed}</td></tr>
              <tr><td>Description</td><td>{i.weather[0].description}</td></tr>
              </tbody></table></div>
        )
    });
    return (
      <div>
          {info}
      </div>
    );
  }
}

export default Info;
