import React, { Component } from "react";
import "./App.css";

class App extends Component {
  getWeather= async(e)=>{
    e.preventDefault();
    console.log("hi");
    const city = e.target.elements.city.value;
    const country = e.target.elements.country.value;
    console.log(city,country);
    const apicall=await fetch(`http://api.openweathermap.org/data/2.5/weather?q=${city},${country}&appid=ea695ae24c843680f5719fc33376a096&units=metric`);
    const data= await apicall.json();
    console.log(data);
  }
  render() {
    return (
      <div className="App">
        <header className="App-header">
          <h2>Weather App</h2>
        </header>
        <form onSubmit={this.getWeather(e.city.value,e.country.value)}>
          <label>City</label>
          <input type="text" name="city" placeholder="Enter City"/>
          <label>Country</label>
          <input type="text" name="city" placeholder="Enter Country"/>
          <button>Get Weather</button>
        </form>
      </div>
    );
  }
}

export default App;
