import React, { Component } from "react";
import "./App.css";

class App extends Component {
  constructor() {
    super();
    this.state = {
      city: '',
      state:'',
      country: '',
      error:''
    };
    this.handleChange = this.handleChange.bind(this);
  }

  handleChange(e){
    this.setState({[e.target.name]: e.target.value});}

    getLocation=async()=>{
      const apilocationcall=await fetch("https://api.db-ip.com/v2/free/self");
     const data=await apilocationcall.json();
     console.log(data.city, data.stateProv, data.countryName);
     this.setState(
       {city:data.city,
       state:data.stateProv,
       country:data.countryName}
     );
    }

  getWeather= async()=>{
    try{
    const apicall=await fetch(`http://api.openweathermap.org/data/2.5/forecast?q=${this.state.city},${this.state.country}&appid=1db31d64a574b28b83f890456cee97ce`);
    const data= await apicall.json();
    console.log(data);}
    catch(e){
      console.log(e);
      this.setState({
        city:'',
        state:'',
        country:''
      })
    }
  }

  render() {
    this.getLocation();
    const {city,state,country}=this.state;
    return (
      <div className="App">
        <header className="App-header">
          <h2>Weather App</h2>
        </header>
        <h3>You are in {city}, {state}, {country}!</h3>
        {this.getWeather()}       
        {this.state.city==='' && (
          <div>
            <form onSubmit={this.getWeather}>
              <label htmlFor="city">City</label>
              <input type="text" value={this.state.city} placeholder="Enter city" />
              <label htmlFor="state">State</label>
              <input type="text" value={this.state.state} placeholder="Enter state" />
              <label htmlFor="country">Country</label>
              <input type="text" value={this.state.country} placeholder="Enter country" />
              <button type="submit">Get Weather</button>
            </form>
          </div>
        )}
      </div>
    );
  }
}

export default App;
