import React, { Component } from "react";
import "./App.css";

class App extends Component {
  constructor() {
    super();
    this.state = {
      city: '',
      country: '',
    };
    this.handleChange = this.handleChange.bind(this);
  }

  handleChange(e){
    this.setState({[e.target.name]: e.target.value});}

    getLocation=async()=>{
      const apilocationcall=await fetch("https://api.db-ip.com/v2/free/self");
     const data=await apilocationcall.json();
     console.log(data.city, data.countryName);
     this.setState(
       {city:data.city,
       country:data.countryName}
     );
    const apicall=await fetch(`http://api.openweathermap.org/data/2.5/forecast?q=${this.state.city},${this.state.country}&appid=1db31d64a574b28b83f890456cee97ce`);
    const wdata= await apicall.json();
    console.log(wdata);
    if(wdata.message==='city not found'){
      this.setState({
        city:'',
        country:''
      })
    }}

  getWeather= async(e)=>{
    this.setState({
      city:e.target.elements.city.value,
      country:e.target.elements.country.value
    })
    const apicall=await fetch(`http://api.openweathermap.org/data/2.5/forecast?q=${this.state.city},${this.state.country}&appid=1db31d64a574b28b83f890456cee97ce`);
    const data= await apicall.json();
    console.log(data);
    if(data.message==='city not found'){
      this.setState({
        city:'',
        country:''
      })
    }
  }

  componentDidMount(){
    this.getLocation();
  }

  render() {
    var {city,country}=this.state;
    return (
      <div className="App">
        <header className="App-header">
          <h2>Weather App</h2>
        </header>
        <h3>You are in {city}, {country}!</h3>
          <div>
            <form onSubmit={this.getWeather}>
              <label htmlFor="city">City</label>
              <input type="text" name="city" value={this.state.city} placeholder="Enter city" onChange={this.handleChange}/>
              <label htmlFor="country">Country</label>
              <input type="text" name="country" value={this.state.country} placeholder="Enter country" onChange={this.handleChange}/>
              <button type="submit">Get Weather</button>
            </form>
          </div>
      </div>
    );
  }
}

export default App;
